import { useState, UseEffect } from 'react';
import { Alert, StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';
import * as LocalAuthentication from 'expo-local-authentication';

export default function Login() {
    const [user, setUser] = useState('');
    const [senha, setSenha] = useState('');
    const [autenticado, setAutenticado] = useState(false);

    async function verificarAutenticacao() {
        const compativel = await LocalAuthentication.hasHardwareAsync();
        const tipos = await LocalAuthentication.supportedAuthenticationTypesAsync();
    }

    async function handleAuth() {
        const temBiometria = await LocalAuthentication.isEnrolledAsync();

        if (!temBiometria) {
            return Alert.alert('Login', 'Nenhuma biometria encontrada. Cadastre uma biometria e retorne ao nosso app!')
        }

        const auth = await LocalAuthentication.authenticateAsync({
            promptMessage: 'Login com Biometria',
            fallbackLabel: 'Biometria não reconhecida'
        });

        if (auth.success) {
            setAutenticado(true);
        } else {
            Alert.alert('Login', 'Falha na autenticação');
        }
    };

    function handleLogin() {
        if (username === 'user' && password === '1234') {
            handleAuth();
        } else {
            Alert.alert('Login', 'Usuário ou senha inválidos');
        }
    }

    useEffect(() => {
        verificarAutenticacao();
    }, []);

    return (
        <View style={styles.container}>
            <TextInput
                style={styles.input}
                placeholder="Username"
                onChangeText={setUser}
                value={user}
            />
            <TextInput
                style={styles.input}
                placeholder="Senha"
                onChangeText={setSenha}
                value={senha}
                secureTextEntry={true}
            />
            <TouchableOpacity activeOpacity={0.75} onPress={handleAuth} style={styles.botao}>
                <Text style={styles.botaoText}>
                    Entrar
                </Text>
            </TouchableOpacity>
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'pink'
    },
    input: {
        borderWidth: 1,
        borderColor: 'gray',
        borderRadius: 5,
        padding: 10,
        marginVertical: 5,
        width: '80%',
    },
    botao: {
        backgroundColor: '#1E90FF',
        padding: 10,
        borderRadius: 5,
        marginVertical: 10,
    },
    botaoText: {
        color: '#fff',
        fontWeight: 'bold',
        textAlign: 'center',
    },
});
